script:
frappe.ui.form.on('Appraisal Goal', {
    kra: function (frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.kra) {
            frappe.call({
                method: "victoryfarmsdeveloper.victoryfarmsdeveloper.customization.appraisal.appraisal.CustomAppraisal.fetch_kra_description",
                args: {
                    title: row.kra
                },
                callback: function (r) {
                    if (r.message) {
                        frappe.model.set_value(cdt, cdn, "did_the_employee", r.message);
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Appraisal', {
    before_load: function (frm) {
        frm.set_value('custom_score_def_image', '/private/files/1-5 Score Definitions.jpg');
    }
});

frappe.ui.form.on('Appraisal Goal', {
     score(frm, cdt, cdn) {
        let d = locals[cdt][cdn]; 
        if (d.score) {
            if (d.score < 1) {
                frappe.throw(__("Score should be between 1 to 5")) 
            }
            d.score = Math.round(d.score);
        }
    },
    // refresh: function(frm) {
    //     $("[data-kra='start_date']").attr("kra", "Please mention the order begin date");
    // }
})

frappe.ui.form.on('Department Appraisal', {
    before_load: function (frm) {
        frm.set_value('custom_score_def_image', '/private/files/1-5 Score Definitions.jpg');
    }
});
