script:
frappe.ui.form.on('Appraisal', {
    before_load: function (frm) {
        frm.set_value('custom_score_def_image', '/private/files/1-5 Score Definitions.jpg');
        frm.set_value('custom_goal_reference_image', '/private/files/goal reference.png');
    }
});

frappe.ui.form.on('Appraisal Goal', {
     score(frm, cdt, cdn) {
        let d = locals[cdt][cdn];
        if (d.score) {
            if (d.score < 1) {
                frappe.throw(__("Score should be between 1 to 5")) 
            }
            frappe.model.set_value(cdt, cdn, "score", Math.round(d.score))
        }
    }
})
