script:
batch_size = 50
draft_invoices = frappe.get_all("Sales Invoice", filters={"docstatus": 0}, fields=["name"], limit=batch_size)

if not draft_invoices:
    frappe.log_error("No more draft invoices to delete.", "No more draft invoices to delete")
else:
    for invoice in draft_invoices:
        frappe.delete_doc("Sales Invoice", invoice.name, force=True)

    frappe.db.commit()
    frappe.enqueue("delete_draft_invoices_worker", queue="long", is_async=True)
