script:
batch_size = 50
draft_invoices = frappe.get_all("Sales Invoice", filters={"docstatus": 0}, fields=["name"], limit=batch_size)

if not draft_invoices:
    frappe.log_error("No more draft invoices to delete.", "No more draft invoices to delete")
else:
    for invoice in draft_invoices:
        frappe.delete_doc("Sales Invoice", invoice.name, force=True)

    frappe.db.commit()
    frappe.enqueue("delete_draft_invoices_worker", queue="long", is_async=True)

for item in doc.items:
    item.description = ""
    if item.custom_item_description:
        item.description = item.custom_item_description

folders = ["Employment", "Disciplinary", "Performance Management", "Payroll Administration"]
parent_folder = f"Home/Employee Documents/{doc.name}"

# Ensure root employee folder exists
if not frappe.db.exists("File", {"file_name": doc.name, "folder": "Home/Employee Documents", "is_folder": 1}):
    frappe.get_doc({
        "doctype": "File",
        "file_name": doc.name,
        "folder": "Home/Employee Documents",
        "is_folder": 1,
        "is_private": 1
    }).insert(ignore_permissions=True)

# Create subfolders
for folder in folders:
    if not frappe.db.exists("File", {"file_name": folder, "folder": parent_folder, "is_folder": 1}):
        frappe.get_doc({
            "doctype": "File",
            "file_name": folder,
            "folder": parent_folder,
            "is_folder": 1,
            "is_private": 1
        }).insert(ignore_permissions=True)
